// admin-dashboard/src/pages/Users.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function Users() {
  const [users, setUsers] = useState([]);
  const [search, setSearch] = useState('');
  const [loading, setLoading] = useState(true);

  function auth() {
    const t = localStorage.getItem('token');
    return t ? { Authorization: 'Bearer ' + t } : {};
  }

  useEffect(() => {
    const loadUsers = async () => {
      setLoading(true);
      try {
        const res = await axios.get('http://localhost:5000/api/admin/users', { headers: auth() });
        // handle different API shapes: array OR { users: [...] } OR { data: [...] }
        const usersData = Array.isArray(res.data)
          ? res.data
          : res.data.users || res.data.data || [];
        setUsers(usersData);
      } catch (err) {
        console.error('Users fetch error:', err);
        setUsers([]);
      } finally {
        setLoading(false);
      }
    };
    loadUsers();
  }, []);

  const usersArray = Array.isArray(users) ? users : [];
  const filteredUsers = usersArray.filter((u) =>
    u.name?.toLowerCase().includes(search.toLowerCase())
  );

  if (loading) return <div style={{ padding: 20 }}>Loading users...</div>;

  return (
    <div style={{ padding: 20 }}>
      <h2 style={{ marginBottom: 10 }}>All Users</h2>
      <input
        type="text"
        placeholder="Search user..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        style={{
          padding: 8,
          width: '100%',
          maxWidth: 300,
          marginBottom: 16,
          borderRadius: 6,
          border: '1px solid #ccc',
        }}
      />

      {filteredUsers.length === 0 ? (
        <div style={{ color: '#888' }}>No users found</div>
      ) : (
        <table
          style={{
            width: '100%',
            borderCollapse: 'collapse',
            background: '#fff',
            borderRadius: 8,
            overflow: 'hidden',
          }}
        >
          <thead style={{ background: '#f4f4f4' }}>
            <tr>
              <th style={thStyle}>Name</th>
              <th style={thStyle}>Email</th>
              <th style={thStyle}>Role</th>
              <th style={thStyle}>Joined</th>
            </tr>
          </thead>
          <tbody>
            {filteredUsers.map((u) => (
              <tr key={u._id || u.id}>
                <td style={tdStyle}>{u.name || '—'}</td>
                <td style={tdStyle}>{u.email || '—'}</td>
                <td style={tdStyle}>{u.role || 'User'}</td>
                <td style={tdStyle}>
                  {u.createdAt ? new Date(u.createdAt).toLocaleDateString() : '—'}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

const thStyle = {
  textAlign: 'left',
  padding: 10,
  borderBottom: '1px solid #ddd',
  fontWeight: '600',
};

const tdStyle = {
  padding: 10,
  borderBottom: '1px solid #eee',
};
