import React, {useEffect, useState} from 'react';
import axios from 'axios';
export default function Dashboard(){
  const [users,setUsers]=useState([]); const [posts,setPosts]=useState([]);
  useEffect(()=>{ axios.get('http://localhost:5000/api/admin/users',{headers:auth()}).then(r=>setUsers(r.data)).catch(()=>{}); axios.get('http://localhost:5000/api/admin/posts',{headers:auth()}).then(r=>setPosts(r.data)).catch(()=>{}); },[]);
  function auth(){ const t=localStorage.getItem('token'); return t?{Authorization:'Bearer '+t}:{}; }
  return (
    <div>
      <div style={{display:'flex',gap:16,marginBottom:16}}>
        <div className="card" style={{flex:1}}><div>Total Users</div><div style={{fontSize:24,fontWeight:700}}>{users.length}</div></div>
        <div className="card" style={{flex:1}}><div>Total Posts</div><div style={{fontSize:24,fontWeight:700}}>{posts.length}</div></div>
        <div className="card" style={{flex:1}}><div>Total Comments</div><div style={{fontSize:24,fontWeight:700}}>{posts.reduce((acc,p)=>acc+(p.comments?.length||0),0)}</div></div>
      </div>
      <div className="card"><h3>Recent Posts</h3>{posts.slice(0,5).map(p=>(<div key={p._id} style={{padding:8,borderBottom:'1px solid #eee'}}>{p.title} by {p.author?.name}</div>))}</div>
    </div>
  )
}
