// admin-dashboard/src/pages/Posts.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function Posts() {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);

  function auth() {
    const t = localStorage.getItem('token');
    return t ? { Authorization: 'Bearer ' + t } : {};
  }

  async function fetchPosts() {
    setLoading(true);
    try {
      const res = await axios.get('http://localhost:5000/api/admin/posts', { headers: auth() });
      const data = Array.isArray(res.data) ? res.data : res.data.posts || res.data.data || [];
      setPosts(data);
    } catch (err) {
      console.error('Posts fetch error:', err);
      setPosts([]);
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => {
    fetchPosts();
  }, []);

  async function deletePost(id) {
    if (!window.confirm('Delete this post?')) return;
    try {
      await axios.delete(`http://localhost:5000/api/admin/posts/${id}`, { headers: auth() });
      alert('Post deleted');
      fetchPosts();
    } catch (err) {
      console.error(err);
      alert('Failed to delete post');
    }
  }

  if (loading) return <div style={{ padding: 20 }}>Loading posts...</div>;

  const postsArray = Array.isArray(posts) ? posts : [];

  return (
    <div style={{ padding: 20 }}>
      <h2 style={{ marginBottom: 10 }}>Manage Posts</h2>
      {postsArray.length === 0 ? (
        <div>No posts found</div>
      ) : (
        <table
          style={{
            width: '100%',
            borderCollapse: 'collapse',
            background: '#fff',
            borderRadius: 8,
            overflow: 'hidden',
          }}
        >
          <thead style={{ background: '#f4f4f4' }}>
            <tr>
              <th style={thStyle}>Title</th>
              <th style={thStyle}>Author</th>
              <th style={thStyle}>Likes</th>
              <th style={thStyle}>Comments</th>
              <th style={thStyle}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {postsArray.map((p) => (
              <tr key={p._id || p.id}>
                <td style={tdStyle}>{p.title}</td>
                <td style={tdStyle}>{p.author?.name || 'Unknown'}</td>
                <td style={tdStyle}>{p.likes?.length || 0}</td>
                <td style={tdStyle}>{p.comments?.length || 0}</td>
                <td style={tdStyle}>
                  <button
                    onClick={() => deletePost(p._id || p.id)}
                    style={{
                      background: '#dc2626',
                      color: 'white',
                      border: 'none',
                      padding: '6px 10px',
                      borderRadius: 4,
                      cursor: 'pointer',
                    }}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

const thStyle = {
  textAlign: 'left',
  padding: 10,
  borderBottom: '1px solid #ddd',
  fontWeight: '600',
};

const tdStyle = {
  padding: 10,
  borderBottom: '1px solid #eee',
};
