import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function Dashboard() {
  const [users, setUsers] = useState([]);
  const [posts, setPosts] = useState([]);

  // Function to get auth header
  function auth() {
    const token = localStorage.getItem('token');
    return token ? { Authorization: 'Bearer ' + token } : {};
  }

  // Fetch users and posts when component loads
  useEffect(() => {
    const fetchData = async () => {
      try {
        const userRes = await axios.get('http://localhost:5000/api/admin/users', { headers: auth() });
        setUsers(userRes.data || []);

        const postRes = await axios.get('http://localhost:5000/api/admin/posts', { headers: auth() });
        setPosts(postRes.data || []);
      } catch (err) {
        console.error('Error loading dashboard data:', err);
      }
    };
    fetchData();
  }, []);

  // Calculate total comments
  const totalComments = posts.reduce((acc, p) => acc + (p.comments?.length || 0), 0);

  return (
    <div style={{ padding: '20px' }}>
      {/* Top Summary Cards */}
      <div style={{ display: 'flex', gap: 16, marginBottom: 16 }}>
        <div className="card" style={cardStyle}>
          <div>Total Users</div>
          <div style={countStyle}>{users.length}</div>
        </div>
        <div className="card" style={cardStyle}>
          <div>Total Posts</div>
          <div style={countStyle}>{posts.length}</div>
        </div>
        <div className="card" style={cardStyle}>
          <div>Total Comments</div>
          <div style={countStyle}>{totalComments}</div>
        </div>
      </div>

      {/* Recent Posts Section */}
      <div className="card" style={{ padding: 16 }}>
        <h3 style={{ marginBottom: 12 }}>Recent Posts</h3>
        {posts.length > 0 ? (
          posts.slice(0, 5).map((p) => (
            <div
              key={p._id}
              style={{
                padding: 8,
                borderBottom: '1px solid #eee',
                fontSize: 15,
              }}
            >
              <strong>{p.title}</strong> <span style={{ color: '#555' }}>by {p.author?.name || 'Unknown'}</span>
            </div>
          ))
        ) : (
          <div style={{ padding: 8, color: '#888' }}>No posts available</div>
        )}
      </div>
    </div>
  );
}

// Basic inline styles for cards
const cardStyle = {
  flex: 1,
  padding: 16,
  border: '1px solid #ddd',
  borderRadius: 10,
  boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
  background: '#fff',
};

const countStyle = {
  fontSize: 26,
  fontWeight: 700,
  marginTop: 6,
};
