// admin-dashboard/src/pages/Posts.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function Posts() {
  const [posts, setPosts] = useState([]);
  const [editing, setEditing] = useState(null);
  const [editData, setEditData] = useState({ title: '', content: '' });

  function auth() {
    const t = localStorage.getItem('token');
    return t ? { Authorization: 'Bearer ' + t } : {};
  }

  async function fetchPosts() {
    try {
      const res = await axios.get('http://localhost:5000/api/admin/posts', { headers: auth() });
      const data = Array.isArray(res.data) ? res.data : res.data.posts || [];
      setPosts(data);
    } catch (err) {
      console.error('Fetch posts failed:', err);
      setPosts([]);
    }
  }

  useEffect(() => {
    fetchPosts();
  }, []);

  async function deletePost(id) {
    if (!window.confirm('Delete this post?')) return;
    try {
      await axios.delete(`http://localhost:5000/api/admin/posts/${id}`, { headers: auth() });
      alert('Post deleted');
      fetchPosts();
    } catch (err) {
      console.error(err);
      alert('Failed to delete post');
    }
  }

  function startEdit(post) {
    setEditing(post._id);
    setEditData({ title: post.title, content: post.content });
  }

  async function saveEdit(id) {
    try {
      await axios.put(`http://localhost:5000/api/admin/posts/${id}`, editData, { headers: auth() });
      alert('Post updated');
      setEditing(null);
      fetchPosts();
    } catch (err) {
      console.error(err);
      alert('Failed to update post');
    }
  }

  const postsArray = Array.isArray(posts) ? posts : [];

  return (
    <div style={{ padding: 20 }}>
      <h2>Manage Posts</h2>

      {postsArray.length === 0 ? (
        <div>No posts found</div>
      ) : (
        <table
          style={{
            width: '100%',
            borderCollapse: 'collapse',
            background: '#fff',
            borderRadius: 8,
            overflow: 'hidden',
          }}
        >
          <thead style={{ background: '#f4f4f4' }}>
            <tr>
              <th style={th}>Title</th>
              <th style={th}>Author</th>
              <th style={th}>Likes</th>
              <th style={th}>Comments</th>
              <th style={th}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {postsArray.map((p) => (
              <tr key={p._id}>
                <td style={td}>
                  {editing === p._id ? (
                    <input
                      value={editData.title}
                      onChange={(e) => setEditData({ ...editData, title: e.target.value })}
                      style={{ width: '100%', padding: 6 }}
                    />
                  ) : (
                    p.title
                  )}
                </td>
                <td style={td}>{p.author?.name || 'Unknown'}</td>
                <td style={td}>{p.likes?.length || 0}</td>
                <td style={td}>{p.comments?.length || 0}</td>
                <td style={td}>
                  {editing === p._id ? (
                    <>
                      <button
                        onClick={() => saveEdit(p._id)}
                        style={btnGreen}
                      >
                        Save
                      </button>
                      <button
                        onClick={() => setEditing(null)}
                        style={btnGray}
                      >
                        Cancel
                      </button>
                    </>
                  ) : (
                    <>
                      <button onClick={() => startEdit(p)} style={btnBlue}>
                        Edit
                      </button>
                      <button onClick={() => deletePost(p._id)} style={btnRed}>
                        Delete
                      </button>
                    </>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

const th = { textAlign: 'left', padding: 10, borderBottom: '1px solid #ddd' };
const td = { padding: 10, borderBottom: '1px solid #eee' };

const btnRed = { background: '#dc2626', color: '#fff', border: 'none', padding: '6px 10px', borderRadius: 4, cursor: 'pointer', marginRight: 4 };
const btnBlue = { background: '#2563eb', color: '#fff', border: 'none', padding: '6px 10px', borderRadius: 4, cursor: 'pointer', marginRight: 4 };
const btnGreen = { background: '#16a34a', color: '#fff', border: 'none', padding: '6px 10px', borderRadius: 4, cursor: 'pointer', marginRight: 4 };
const btnGray = { background: '#6b7280', color: '#fff', border: 'none', padding: '6px 10px', borderRadius: 4, cursor: 'pointer' };
