// admin-dashboard/src/pages/Users.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function Users() {
  const [users, setUsers] = useState([]);
  const [search, setSearch] = useState('');
  const [loading, setLoading] = useState(true);
  const [editing, setEditing] = useState(null);
  const [editData, setEditData] = useState({ name: '', role: '' });

  function auth() {
    const t = localStorage.getItem('token');
    return t ? { Authorization: 'Bearer ' + t } : {};
  }

  async function fetchUsers() {
    setLoading(true);
    try {
      const res = await axios.get('http://localhost:5000/api/admin/users', { headers: auth() });
      const data = Array.isArray(res.data) ? res.data : res.data.users || res.data.data || [];
      setUsers(data);
    } catch (err) {
      console.error('Users fetch error:', err);
      setUsers([]);
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => {
    fetchUsers();
  }, []);

  async function deleteUser(id) {
    if (!window.confirm('Are you sure you want to delete this user?')) return;
    try {
      await axios.delete(`http://localhost:5000/api/admin/users/${id}`, { headers: auth() });
      alert('User deleted successfully');
      fetchUsers();
    } catch (err) {
      console.error('Delete failed:', err);
      alert('Failed to delete user');
    }
  }

  function startEdit(user) {
    setEditing(user._id);
    setEditData({ name: user.name, role: user.role });
  }

  async function saveEdit(id) {
    try {
      await axios.put(`http://localhost:5000/api/admin/users/${id}`, editData, { headers: auth() });
      alert('User updated successfully');
      setEditing(null);
      fetchUsers();
    } catch (err) {
      console.error('Update failed:', err);
      alert('Failed to update user');
    }
  }

  const filtered = (Array.isArray(users) ? users : []).filter((u) =>
    u.name?.toLowerCase().includes(search.toLowerCase())
  );

  if (loading) return <div style={{ padding: 20 }}>Loading users...</div>;

  return (
    <div style={{ padding: 20 }}>
      <h2 style={{ marginBottom: 10 }}>Manage Users</h2>
      <input
        type="text"
        placeholder="Search user..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        style={{
          padding: 8,
          width: '100%',
          maxWidth: 300,
          marginBottom: 16,
          borderRadius: 6,
          border: '1px solid #ccc',
        }}
      />

      {filtered.length === 0 ? (
        <div style={{ color: '#888' }}>No users found</div>
      ) : (
        <table
          style={{
            width: '100%',
            borderCollapse: 'collapse',
            background: '#fff',
            borderRadius: 8,
            overflow: 'hidden',
          }}
        >
          <thead style={{ background: '#f4f4f4' }}>
            <tr>
              <th style={thStyle}>Name</th>
              <th style={thStyle}>Email</th>
              <th style={thStyle}>Role</th>
              <th style={thStyle}>Joined</th>
              <th style={thStyle}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {filtered.map((u) => (
              <tr key={u._id || u.id}>
                <td style={tdStyle}>
                  {editing === u._id ? (
                    <input
                      value={editData.name}
                      onChange={(e) => setEditData({ ...editData, name: e.target.value })}
                      style={{ padding: 5, width: '100%' }}
                    />
                  ) : (
                    u.name || '—'
                  )}
                </td>
                <td style={tdStyle}>{u.email || '—'}</td>
                <td style={tdStyle}>
                  {editing === u._id ? (
                    <select
                      value={editData.role}
                      onChange={(e) => setEditData({ ...editData, role: e.target.value })}
                      style={{ padding: 5 }}
                    >
                      <option value="User">User</option>
                      <option value="Admin">Admin</option>
                    </select>
                  ) : (
                    u.role || 'User'
                  )}
                </td>
                <td style={tdStyle}>
                  {u.createdAt ? new Date(u.createdAt).toLocaleDateString() : '—'}
                </td>
                <td style={tdStyle}>
                  {editing === u._id ? (
                    <>
                      <button onClick={() => saveEdit(u._id)} style={btnGreen}>Save</button>
                      <button onClick={() => setEditing(null)} style={btnGray}>Cancel</button>
                    </>
                  ) : (
                    <>
                      <button onClick={() => startEdit(u)} style={btnBlue}>Edit</button>
                      <button onClick={() => deleteUser(u._id)} style={btnRed}>Delete</button>
                    </>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

const thStyle = { textAlign: 'left', padding: 10, borderBottom: '1px solid #ddd', fontWeight: '600' };
const tdStyle = { padding: 10, borderBottom: '1px solid #eee' };

const btnRed = { background: '#dc2626', color: '#fff', border: 'none', padding: '6px 10px', borderRadius: 4, cursor: 'pointer', marginRight: 4 };
const btnBlue = { background: '#2563eb', color: '#fff', border: 'none', padding: '6px 10px', borderRadius: 4, cursor: 'pointer', marginRight: 4 };
const btnGreen = { background: '#16a34a', color: '#fff', border: 'none', padding: '6px 10px', borderRadius: 4, cursor: 'pointer', marginRight: 4 };
const btnGray = { background: '#6b7280', color: '#fff', border: 'none', padding: '6px 10px', borderRadius: 4, cursor: 'pointer' };
