// frontend/src/pages/Profile.js
import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Profile() {
  const stored = JSON.parse(localStorage.getItem("user") || "null");
  const [user, setUser] = useState(stored);
  const [name, setName] = useState(user?.name || "");
  const [bio, setBio] = useState(user?.bio || "");
  const [avatar, setAvatar] = useState(null);
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);

  // ✅ Fetch profile once
  useEffect(() => {
    fetchProfile();
  }, []);

  // ✅ Fetch profile
  async function fetchProfile() {
    const token = localStorage.getItem("token");
    if (!token) return;
    try {
      const res = await axios.get("http://localhost:5000/api/users/me", {
        headers: { Authorization: "Bearer " + token },
      });
      setUser(res.data);
      setName(res.data.name);
      setBio(res.data.bio || "");
      fetchPosts(res.data._id); // ✅ Fetch posts only after user is loaded
    } catch (e) {
      console.error("Error fetching profile:", e);
      alert("Failed to load profile");
    } finally {
      setLoading(false);
    }
  }

  // ✅ Fetch posts for the logged-in user
  async function fetchPosts(userId) {
    try {
      const res = await axios.get("http://localhost:5000/api/posts");
      const filtered = res.data.filter(
        (p) => p.author?._id === userId || p.author === userId
      );
      setPosts(filtered);
    } catch (err) {
      console.error("Error fetching posts:", err);
    }
  }

  // ✅ Save profile updates
  async function save(e) {
    e.preventDefault();
    const token = localStorage.getItem("token");
    if (!token) return;

    try {
      let imageBase64 = null;
      if (avatar) {
        const reader = new FileReader();
        reader.readAsDataURL(avatar);
        await new Promise((res) => (reader.onload = () => res()));
        imageBase64 = reader.result;
      }

      const res = await axios.put(
        "http://localhost:5000/api/users/me",
        { name, bio, imageBase64 },
        { headers: { Authorization: "Bearer " + token } }
      );

      localStorage.setItem(
        "user",
        JSON.stringify({
          id: res.data._id,
          name: res.data.name,
          email: res.data.email,
          role: res.data.role,
        })
      );

      alert("Profile updated successfully");
      fetchProfile();
    } catch (err) {
      console.error("Error saving profile:", err);
      alert("Failed to save profile");
    }
  }

  // ✅ Delete post
  async function delPost(id) {
    if (!window.confirm("Delete this post?")) return;
    const token = localStorage.getItem("token");
    try {
      await axios.delete(`http://localhost:5000/api/posts/${id}`, {
        headers: { Authorization: "Bearer " + token },
      });
      setPosts((p) => p.filter((x) => x._id !== id));
    } catch (err) {
      console.error("Error deleting post:", err);
      alert("Failed to delete post");
    }
  }

  if (loading) return <div className="card">Loading...</div>;

  return (
    <div className="card" style={{ maxWidth: 900, margin: "24px auto" }}>
      <h2>My Profile</h2>
      <form onSubmit={save} className="space-y-4">
        <div style={{ display: "flex", gap: 16, alignItems: "center" }}>
          <div
            style={{
              width: 96,
              height: 96,
              borderRadius: 12,
              background: "#eef2ff",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              fontSize: 32,
              overflow: "hidden",
            }}
          >
            {user?.avatar ? (
              <img
                src={user.avatar}
                alt="Avatar"
                style={{ width: "100%", height: "100%", objectFit: "cover" }}
              />
            ) : (
              user?.name?.[0]?.toUpperCase()
            )}
          </div>
          <div style={{ flex: 1 }}>
            <input
              className="input"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
            <textarea
              className="input"
              rows={3}
              value={bio}
              onChange={(e) => setBio(e.target.value)}
              style={{ marginTop: 8 }}
            />
            <input
              type="file"
              accept="image/*"
              onChange={(e) => setAvatar(e.target.files[0])}
              style={{ marginTop: 8 }}
            />
          </div>
        </div>
        <div style={{ display: "flex", justifyContent: "flex-end" }}>
          <button className="btn">Save Profile</button>
        </div>
      </form>

      <h3 style={{ marginTop: 20 }}>My Posts</h3>
      <div style={{ marginTop: 8 }}>
        {posts.length === 0 ? (
          <p>No posts yet.</p>
        ) : (
          posts.map((p) => (
            <div
              key={p._id}
              className="card"
              style={{
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
                marginBottom: 8,
              }}
            >
              <div>
                <div style={{ fontWeight: 700 }}>{p.title}</div>
                <div style={{ color: "#64748b", fontSize: 13 }}>
                  {p.category} ·{" "}
                  {new Date(p.createdAt).toLocaleDateString()}
                </div>
              </div>
              <div className="post-actions" style={{ display: "flex", gap: 8 }}>
                <button
                  onClick={() =>
                    (window.location.href = "/create?edit=" + p._id)
                  }
                  className="btn"
                  style={{ background: "#10b981" }}
                >
                  Edit
                </button>
                <button
                  onClick={() => delPost(p._id)}
                  className="btn"
                  style={{ background: "#ef4444" }}
                >
                  Delete
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
