// frontend/src/pages/Profile.js
import React, {useEffect, useState} from 'react';
import axios from 'axios';
export default function Profile(){
  const stored = JSON.parse(localStorage.getItem('user')||'null');
  const [user,setUser]=useState(stored);
  const [name,setName]=useState(user?.name||''); const [bio,setBio]=useState(user?.bio||''); const [avatar,setAvatar]=useState(null);
  const [posts,setPosts]=useState([]);

  useEffect(()=>{ fetchProfile(); fetchPosts(); },[]);

  async function fetchProfile(){
    const token = localStorage.getItem('token');
    try{ const res = await axios.get('http://localhost:5000/api/users/me', { headers: { Authorization: 'Bearer ' + token } }); setUser(res.data); setName(res.data.name); setBio(res.data.bio||''); }catch(e){}
  }
  async function fetchPosts(){
    const res = await axios.get('http://localhost:5000/api/posts'); setPosts(res.data.filter(p=>p.author?._id === (user?._id||user?.id)));
  }

  async function save(e){
    e.preventDefault();
    const token = localStorage.getItem('token');
    try{
      let imageBase64 = null;
      if(avatar){ const reader = new FileReader(); reader.readAsDataURL(avatar); await new Promise(res=> reader.onload = ()=> res()); imageBase64 = reader.result; }
      const res = await axios.put('http://localhost:5000/api/users/me', { name, bio, imageBase64 }, { headers: { Authorization: 'Bearer ' + token } });
      localStorage.setItem('user', JSON.stringify({ id: res.data._id, name: res.data.name, email: res.data.email, role: res.data.role }));
      alert('Profile updated'); fetchProfile();
    }catch(err){ console.error(err); alert('Error'); }
  }

  async function delPost(id){
    if(!confirm('Delete post?')) return;
    const token = localStorage.getItem('token');
    await axios.delete('http://localhost:5000/api/posts/'+id, { headers: { Authorization: 'Bearer ' + token } });
    setPosts(p=>p.filter(x=>x._id!==id));
  }

  return (
    <div className="card" style={{maxWidth:900,margin:'24px auto'}}>
      <h2>My Profile</h2>
      <form onSubmit={save} className="space-y-4">
        <div style={{display:'flex',gap:16,alignItems:'center'}}>
          <div style={{width:96,height:96,borderRadius:12,background:'#eef2ff',display:'flex',alignItems:'center',justifyContent:'center',fontSize:32}}>{user?.name?.[0]?.toUpperCase()}</div>
          <div style={{flex:1}}>
            <input className="input" value={name} onChange={e=>setName(e.target.value)} />
            <textarea className="input" rows={3} value={bio} onChange={e=>setBio(e.target.value)} style={{marginTop:8}} />
            <input type="file" accept="image/*" onChange={e=>setAvatar(e.target.files[0])} style={{marginTop:8}} />
          </div>
        </div>
        <div style={{display:'flex',justifyContent:'flex-end'}}><button className="btn">Save Profile</button></div>
      </form>

      <h3 style={{marginTop:20}}>My Posts</h3>
      <div style={{marginTop:8}}>
        {posts.map(p=>(
          <div key={p._id} className="card" style={{display:'flex',justifyContent:'space-between',alignItems:'center'}}>
            <div>
              <div style={{fontWeight:700}}>{p.title}</div>
              <div style={{color:'#64748b',fontSize:13}}>{p.category} Â· {new Date(p.createdAt).toLocaleDateString()}</div>
            </div>
            <div className="post-actions">
              <button onClick={()=>window.location.href='/create?edit='+p._id} className="btn" style={{background:'#10b981'}}>Edit</button>
              <button onClick={()=>delPost(p._id)} className="btn" style={{background:'#ef4444'}}>Delete</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  )
}
