import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

export default function CreatePost({ token, user }) {
  const { editId } = useParams();
  const navigate = useNavigate();
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [category, setCategory] = useState('');
  const [tags, setTags] = useState('');
  const [imageBase64, setImageBase64] = useState('');
  const [imagePreview, setImagePreview] = useState('');
  const [comments, setComments] = useState([]);
  const [likes, setLikes] = useState([]);

  // load post when editing
  useEffect(() => {
    if (editId) {
      axios.get(`/api/posts/${editId}`).then((res) => {
        const p = res.data;
        setTitle(p.title || '');
        setContent(p.content || '');
        setCategory(p.category || '');
        setTags(p.tags || '');
        setImagePreview(p.image || '');
        setComments(p.comments || []);
        setLikes(p.likes || []);
      });
    }
  }, [editId]);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onloadend = () => {
      setImageBase64(reader.result);
      setImagePreview(reader.result);
    };
    reader.readAsDataURL(file);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const payload = { title, content, category, tags, imageBase64 };
      const headers = { Authorization: `Bearer ${token}` };
      if (editId) {
        await axios.put(`/api/posts/${editId}`, payload, { headers });
        alert('Post updated successfully');
      } else {
        await axios.post('/api/posts', payload, { headers });
        alert('Post created successfully');
      }
      navigate('/dashboard');
    } catch (err) {
      console.error(err);
      alert('Error saving post');
    }
  };

  // --- ADMIN ACTIONS ---
  const handleRemoveComment = async (commentId) => {
    if (!window.confirm('Delete this comment?')) return;
    try {
      await axios.delete(`/api/admin/post/${editId}/comment/${commentId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setComments((prev) => prev.filter((c) => c._id !== commentId));
    } catch (err) {
      console.error(err);
      alert('Failed to remove comment');
    }
  };

  const handleRemoveLike = async (userId) => {
    if (!window.confirm('Remove this like?')) return;
    try {
      await axios.delete(`/api/admin/post/${editId}/like/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setLikes((prev) => prev.filter((id) => id !== userId));
    } catch (err) {
      console.error(err);
      alert('Failed to remove like');
    }
  };

  return (
    <div className="max-w-3xl mx-auto mt-8 bg-white p-6 shadow-md rounded-xl">
      <h2 className="text-2xl font-bold mb-4">
        {editId ? 'Edit Post' : 'Create New Post'}
      </h2>

      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Title"
          className="w-full border p-2 mb-3 rounded"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />

        <textarea
          placeholder="Content"
          className="w-full border p-2 mb-3 rounded h-40"
          value={content}
          onChange={(e) => setContent(e.target.value)}
          required
        />

        <input
          type="text"
          placeholder="Category"
          className="w-full border p-2 mb-3 rounded"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
        />

        <input
          type="text"
          placeholder="Tags (comma separated)"
          className="w-full border p-2 mb-3 rounded"
          value={tags}
          onChange={(e) => setTags(e.target.value)}
        />

        <input type="file" onChange={handleImageChange} className="mb-3" />
        {imagePreview && (
          <img
            src={imagePreview}
            alt="Preview"
            className="w-48 h-48 object-cover rounded mb-3"
          />
        )}

        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          {editId ? 'Update Post' : 'Create Post'}
        </button>
      </form>

      {/* --- ADMIN SECTION: COMMENTS & LIKES --- */}
      {editId && user?.role === 'admin' && (
        <div className="mt-8 border-t pt-6">
          <h3 className="text-xl font-semibold mb-3">Admin Controls</h3>

          {/* Comments */}
          <h4 className="font-semibold mb-2">Comments</h4>
          {comments.length ? (
            comments.map((c) => (
              <div
                key={c._id}
                className="flex justify-between items-center border-b py-2"
              >
                <p className="text-gray-700">{c.text}</p>
                <button
                  onClick={() => handleRemoveComment(c._id)}
                  className="text-red-600 hover:text-red-800"
                >
                  Remove
                </button>
              </div>
            ))
          ) : (
            <p className="text-gray-500 italic">No comments yet</p>
          )}

          {/* Likes */}
          <h4 className="font-semibold mt-5 mb-2">Likes</h4>
          {likes.length ? (
            likes.map((uid) => (
              <div
                key={uid}
                className="flex justify-between items-center border-b py-2"
              >
                <p className="text-gray-700">User ID: {uid}</p>
                <button
                  onClick={() => handleRemoveLike(uid)}
                  className="text-red-600 hover:text-red-800"
                >
                  Remove
                </button>
              </div>
            ))
          ) : (
            <p className="text-gray-500 italic">No likes yet</p>
          )}
        </div>
      )}
    </div>
  );
}
